function main(value, date, format) {
     var calendar = Packages.java.util.Calendar.getInstance();        if(format.equals("millis")){
        calendar.setTimeInMillis(Packages.java.lang.Long.parseLong(date));
        var newDay = calendar.get(Packages.java.util.Calendar.DAY_OF_MONTH)+Packages.java.lang.Integer.parseInt(value);
        calendar.set(Packages.java.util.Calendar.DAY_OF_MONTH, newDay);
        return calendar.getTimeInMillis();
        }
    else {
        var sdf = new Packages.java.text.SimpleDateFormat(format);
        var date1 = sdf.parse(date);
        calendar.setTimeInMillis(date1.getTime());
        var newDay= calendar.get(Packages.java.util.Calendar.DAY_OF_MONTH)+Packages.java.lang.Integer.parseInt(value);
        calendar.set(Packages.java.util.Calendar.DAY_OF_MONTH, newDay);
        return sdf.format(calendar.getTime());
        }
}

function main(variableName) {
    if (contextMap != null) {
        var envId = Packages.java.lang.String.valueOf(contextMap.get('ENV_ID'));
        if (envId == null) {
            return 'ENV_ID is absent in context'
        }
        var environment = contextMap.get(envId);
        if (environment == null) {
            return 'ENVIRONMENT with id' + envId + ' is absent in context'
        }
        var systems = environment.values().toArray();
        for (var i = 0; i < systems.length; i++) {
            var conns = systems[i].values().toArray();
            for (var j = 0; j < conns.length; j++) {
                var connParams = conns[j];
                if (connParams.get(variableName) != null) {
                    return connParams.get(variableName);
                }
            }
        }
        return 'Unknown ENVIRONMENT VARIABLE' + variableName;
    }
}

function main(systemName, variableName) {
    if (contextMap != null) {
        var envId = Packages.java.lang.String.valueOf(contextMap.get('ENV_ID'));
        if (envId == null) {
            return 'ENV_ID is absent in context'
        }
        var environment = contextMap.get(envId);
        if (environment != null && environment.get(systemName) != null) {
                 var connName = variableName;
            var partsOfStr = Packages.org.apache.commons.lang.StringUtils.split(variableName, '.');
            if (partsOfStr != null) {
                connName = partsOfStr[0];
            }
            var conns = environment.get(systemName);
            if (connName === variableName && conns != null) {
                var connsArray = conns.values().toArray();
                for (var j = 0; j < connsArray.length; j++) {
                    var connParams = connsArray[j];
                    if (connParams.get(variableName) != null) {
                        return connParams.get(variableName);
                    }
                }
            } else if(conns != null){
                var connParams = conns.get(connName);
                if (connParams.get(partsOfStr[1]) != null) {
                    return connParams.get(partsOfStr[1]);
                }
            }
        } else {
            return 'Unknown SYSTEM ' + systemName;
        }
        return 'Unknown ENVIRONMENT VARIABLE ' + variableName + ' for system ' + systemName;
    }
}

