function main(random) {
    var possible = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
    var result = '';

    random.split('').forEach(function(item, i, arr) {
        if (item == '$') {
            var n = 0;
		while (n == 0) {
	        n = Math.floor(Math.random()*10);
	    }
	    result = result.concat(n);
        } else if (item == '&') {
            result = result.concat(possible.charAt(Math.floor(Math.random() * possible.length)));
        } else {
            result = result.concat(item);
        }    
    });
    return result;
}

function main(count) {
    var result = '';
    var characters = 'abcdefghijklmnopqrstuvwxyz';
    for (var i = 0; i < count; i++) {
        result += characters.charAt(Math.floor(Math.random() * count));
    }
    return result;
}
